    // current example will utilize all of them

    for (let i = 0; i < numCores; i++) {
      // creating workers and pushing reference in an array
      // these references can be used to receive messages from workers
      workers.push(cluster.fork()); //to receive messages from worker process

      workers[i].on('message', msg => {
        console.log(msg);
      });
    } // process is clustered on a core and process id is assigned


    cluster.on('online', worker => {
      console.log('Worker ' + worker.process.pid + ' is listening');
    }); // if any of the worker process dies then start a new one by simply forking another one

    cluster.on('exit', (worker, code, signal) => {
      console.log('Worker ' + worker.process.pid + ' died with code: ' + code + ', and signal: ' + signal);
      console.log('Starting a new worker');
      workers.push(cluster.fork()); // to receive messages from worker process

      workers[workers.length - 1].on('message', function (message) {
        console.log(message);
      });
    });
  }
  /**
  * Setup an express server and define port to listen all incoming requests for this application
  */


  setUpExpress() {
    // create server
    app.server = http.createServer(app); // logger
    // sockets
    // this.setUpSockets(app.server);
    // parse application/json

    app.use(bodyParser.json());
    app.use(bodyParser.urlencoded({
      extended: false
    }));
    app.disable('x-powered-by'); // routes

    app.use('/api/v1', super.route()); // cookie parser and CSRF Middleware

    app.use(cookieParser()); // app.use(csurf({ cookie : true }));
    // cross origin configuration

    app.use(cors());
    app.use((req, res, next) => {
      res.setHeader('Access-Control-Allow-Origin', '*');
      res.setHeader('Access-Control-Allow-Methods', 'POST,GET,OPTIONS');
      res.setHeader('Access-Control-Allow-Headers', 'Content-Type, Authorization');

      if (req.method == 'OPTIONS') {
        return res.sendStatus(200);
      }

      next();
    });
    app.use('/public', express.static('./public')); // start server

    app.server.listen('8000', () => {
      console.log(`Started server on => http://localhost:${app.server.address().port} for Process Id ${process.pid}`);
    }); // in case of an error

    app.on('error', (appErr, appCtx) => {
      console.error('app error', appErr.stack);
      console.error('on url', appCtx.req.url);
      console.error('with headers', appCtx.req.headers);
    });
  }
