    authService() {
        return new AuthService();
    };
    jwtService() {
        return new JwtService();
    }
    itemsService(){
        return new ItemsService()
    }
    cartService() {
        return new CartService()
    }
    orderService() {
        return new OrderService();
    }
    orderDetailsService() {
        return new OrderDetailsService()
    }

    transactionService() {
        return new TransactionService()
    }
};

export default Service;

