sourcesContent:
import Model from '../models/index';

class JWTCheck {
	constructor() {
		this.authModel = new Model().auth()
	}

	async authCheck(req, res, next) {
		var header = req.headers.authorization || '' // get the auth header
		var token = header.split(/\s+/).pop() || ''

		const check = await this.tokenCheck(token)

		if (!check) {
			next();
		} else {
			return res.status(401).send({
				message: 'Unauthorized, please login first',
			});
		}
	}

	async tokenCheck(tokens) {
		const authToken = await this.authModel.findOne({ token: tokens, raw: true });
		return !!authToken;
	}
}

export default JWTCheck;

