      OrderDetails.belongsTo(models.Items, { foreignKey : 'itemId', as : 'items', onDelete : "CASCADE" });
    }
  }
  OrderDetails.init({
    orderId: DataTypes.INTEGER,
    itemId: DataTypes.INTEGER,
    userId: DataTypes.INTEGER
